package ga.firesoftwares.fireapi.constants;

import java.math.BigDecimal;

import ga.firesoftwares.fireapi.enums.math.EulerDB;
import ga.firesoftwares.fireapi.enums.math.PiDB;
import ga.firesoftwares.fireapi.utils.web.Requests;

/**
 * Constants used in math
 * Note : to use functions/methods, you need to initialize this class with {@code MathConstants mathConstants = new MathConstants();}
 * @author Renaud
 * @since 1.1
 * @version 1.1
 */
public class MathConstants {
	// Variables
	private String piCachedContent, eCachedContent;
	
	public MathConstants() {
		this("");
	}
	public MathConstants(String piCachedContent) {
		this("", "");
	}
	public MathConstants(String piCachedContent, String eCachedContent) {
		this.piCachedContent = piCachedContent;
		this.eCachedContent = eCachedContent;
	}
	
    // -------------------------------------------
    // CONSTANTS
    // -------------------------------------------
	
	// BigDecimals
	/** 
	 * π constant
	 */
	public static final BigDecimal PI = new BigDecimal(3.1415926535897932384626433833);
	/** 
	 * e constant
	 */
	public static final BigDecimal E = new BigDecimal(2.7182818284590452353602874714), EULER = new BigDecimal(2.7182818284590452353602874714);
	/** 
	 * Champernowne's constant
	 */
	public static final BigDecimal CHAMPERNOWNE = new BigDecimal(0.1234567891011121314151617182), C10 = new BigDecimal(0.1234567891011121314151617182);
	/** 
	 * Gold number constant
	 */
	public static final double GOLD_NUMBER = (1 + Math.sqrt(5)) / 2, GOD_NUMBER = (1 + Math.sqrt(5)) / 2, GOD_SECTION = (1 + Math.sqrt(5)) / 2;
	
	// Strings
	/**
	 * π constant to 65532 digits String
	 */
	public static final String PI_65532 = "3.";
	/** 
	 * e constant to 10000 digits String
	 */
	public static final String E_10000 = "2.";
	/**
	 * Champernowne's constant to 65532 digits String
	 */
	public static final String C10_65532 = "0.";
	
	// DB offsets
	private static final int[] PI_DB_OFFSETS = { 1020785, 0, 815 };
	private static final String[] PI_DB_LINKS = { "https://edition.cnn.com/2017/03/13/health/pi-digits-trnd/index.html", "https://stuff.mit.edu/afs/sipb/contrib/pi/pi-billion.txt", "https://www.math.utah.edu/~pa/math/pi.html" };
	private static final int[] E_DB_OFFSETS = { 219, 811, 836 };
	private static final String[] E_DB_LINKS = { "http://www-history.mcs.st-and.ac.uk/HistTopics/e_10000.html", "https://apod.nasa.gov/htmltest/gifcity/e.5mil", "https://www.math.utah.edu/~pa/math/e.html" };

    // -------------------------------------------
    // FUNCTIONS & METHODS
    // -------------------------------------------
	
	// Get from string
	/**
	 * Get π String with specified digits
	 * @param digits Number of digits (max = 65531)
	 * @return π String to specified digits
	 */
	public static String getPiString(int digits) {
		if (digits > 0)
			// Substring 2 because of the 2 first characters "3." of the String
			// PI_65532 + the number of digits specified in this function
			return PI_65532.substring(0, 2 + digits);
		else if (digits < 0)
			return PI.multiply(new BigDecimal(Math.pow(10, digits))).toPlainString();
		// π rounded
		return "3";
	}
	/**
	 * Get e String with specified digits
	 * @param digits Number of digits (max = 9999)
	 * @return e String to specified digits
	 */
	public static String getEString(int digits) {
		if (digits > 0)
			// Substring 2 because of the 2 first characters "2." of the String
			// E_10000 + the number of digits specified in this function
			return E_10000.substring(0, 2 + digits);
		else if (digits < 0)
			return E.multiply(new BigDecimal(Math.pow(10, digits))).toPlainString();
		// e rounded
		return "3";
	}
	/**
	 * Get C10 String with specified digits
	 * @param digits Number of digits (max = 65531)
	 * @return C10 String to specified digits
	 */
	public static String getC10String(int digits) {
		if (digits > 0)
			// Substring 2 because of the 2 first characters "0." of the String
			// C10_65532 + the number of digits specified in this function
			return C10_65532.substring(0, 2 + digits);
		else if (digits < 0)
			return C10.multiply(new BigDecimal(Math.pow(10, digits))).toPlainString();
		// C10 rounded
		return "0";
	}
	
	// Get constants from databases
	/**
	 * Get π String from a specified database
	 * <br>
	 * Note : It's highly recommended to perform this operation in a {@link Thread} because this function performs lengthy operations (depends on your connection, server connection and computer components) so it could make your program freeze.
	 * @param digits Number of digits to download (max depends on database used)
	 * @param database Database used to find constant
	 * @return π String from database
	 * @throws Exception Happens when constant extraction from received data failed
	 */
	public String getPiFromDatabase(int digits, PiDB database) throws Exception {
		return getPiFromDatabase(digits, database, true, true);
	}
	/**
	 * Get π String from a specified database
	 * <br>
	 * Note : It's highly recommended to perform this operation in a {@link Thread} because this function performs lengthy operations (depends on your connection, server connection and computer components) so it could make your program freeze.
	 * @param digits Number of digits to download (max depends on database used)
	 * @param database Database used to find constant
	 * @param refreshfile Do the function need to refresh the file (default = true)
	 * @return π String from database
	 * @throws Exception Happens when constant extraction from received data failed
	 */
	public String getPiFromDatabase(int digits, PiDB database, boolean refreshfile) throws Exception {
		return getPiFromDatabase(digits, database, refreshfile, true);
	}
	/**
	 * Get π String from a specified database
	 * <br>
	 * Note : It's highly recommended to perform this operation in a {@link Thread} because this function performs lengthy operations (depends on your connection, server connection and computer components) so it could make your program freeze.
	 * @param digits Number of digits to download (max depends on database used)
	 * @param database Database used to find constant
	 * @param refreshfile Do the function need to refresh the file (default = true)
	 * @param caching Allow/disallow caching (default = true/allowed) (disabling cache will reduce memory usage of your program but you will need to refresh page each time you need information on it)
	 * @return π String from database
	 * @throws Exception Happens when constant extraction from received data failed
	 */
	public String getPiFromDatabase(int digits, PiDB database, boolean refreshfile, boolean caching) throws Exception {
		if (digits > 0)
			if (!refreshfile && piCachedContent != "" && piCachedContent != null)
				return extractConstantFromDocument(piCachedContent, PI_DB_OFFSETS[database.ordinal()], digits);
			else {
				if (caching) {
					piCachedContent = Requests.getWebFileContent(PI_DB_LINKS[database.ordinal()]);
					return extractConstantFromDocument(piCachedContent, PI_DB_OFFSETS[database.ordinal()], digits);
				} else return extractConstantFromDocument(Requests.getWebFileContent(PI_DB_LINKS[database.ordinal()]), PI_DB_OFFSETS[database.ordinal()], digits);
			}
		else if (digits < 0)
			return PI.multiply(new BigDecimal(Math.pow(10, digits))).toPlainString();
		// π rounded
		return "3";
	}
	
	/**
	 * Get e String from a specified database
	 * <br>
	 * Note : It's highly recommended to perform this operation in a {@link Thread} because this function performs lengthy operations (depends on your connection, server connection and computer components) so it could make your program freeze.
	 * @param digits Number of digits to download (max depends on database used)
	 * @param database Database used to find constant
	 * @return e String from database
	 * @throws Exception Happens when constant extraction from received data failed
	 */
	public String getEFromDatabase(int digits, EulerDB database) throws Exception {
		return getEFromDatabase(digits, database, true, true);
	}
	/**
	 * Get e String from a specified database
	 * <br>
	 * Note : It's highly recommended to perform this operation in a {@link Thread} because this function performs lengthy operations (depends on your connection, server connection and computer components) so it could make your program freeze.
	 * @param digits Number of digits to download (max depends on database used)
	 * @param database Database used to find constant
	 * @param refreshfile Do the function need to refresh the file (default = true)
	 * @return e String from database
	 * @throws Exception Happens when constant extraction from received data failed
	 */
	public String getEFromDatabase(int digits, EulerDB database, boolean refreshfile) throws Exception {
		return getEFromDatabase(digits, database, refreshfile, true);
	}
	/**
	 * Get e String from a specified database
	 * <br>
	 * Note : It's highly recommended to perform this operation in a {@link Thread} because this function performs lengthy operations (depends on your connection, server connection and computer components) so it could make your program freeze.
	 * @param digits Number of digits to download (max depends on database used)
	 * @param database Database used to find constant
	 * @param refreshfile Do the function need to refresh the file (default = true)
	 * @param caching Allow/disallow caching (default = true/allowed) (disabling cache will reduce memory usage of your program but you will need to refresh page each time you need information on it)
	 * @return e String from database
	 * @throws Exception Happens when constant extraction from received data failed
	 */
	public String getEFromDatabase(int digits, EulerDB database, boolean refreshfile, boolean caching) throws Exception {
		if (digits > 0)
			if (!refreshfile && eCachedContent != "" && eCachedContent != null)
				return extractConstantFromDocument(eCachedContent, E_DB_OFFSETS[database.ordinal()], digits);
			else {
				if (caching) {
					eCachedContent = Requests.getWebFileContent(E_DB_LINKS[database.ordinal()]);
					return extractConstantFromDocument(eCachedContent, E_DB_OFFSETS[database.ordinal()], digits);
				} else return extractConstantFromDocument(Requests.getWebFileContent(E_DB_LINKS[database.ordinal()]), E_DB_OFFSETS[database.ordinal()], digits);
			}
		else if (digits < 0)
			return PI.multiply(new BigDecimal(Math.pow(10, digits))).toPlainString();
		// π rounded
		return "3";
	}
	
	/**
	 * Function used to "extract" only the constant from a formatted document
	 * @param data Data from website
	 * @param offset Offset where starts the constant
	 * @param digits Digits of constant
	 * @return Extracted constant from document
	 * @throws Exception Happens if amount of digits is negative or offset is strictly negative
	 */
	private String extractConstantFromDocument(String data, int offset, int digits) throws Exception {
		// Substring 2 because of the 2 first characters "x." of constant + the number of digits precised in this function
		if (digits > 0 && offset >= 0) return data.replace(" ", "").replace(System.lineSeparator(), "").replace((char) 10, (char) 0).replace((char) 13, (char) 0).substring(offset, offset + 2 + digits);
		else throw new Exception();
	}
}
